CREATE TABLE RESPUESTA(
    N_RECLAMO INT,
    RUT VARCHAR(10),
    TEXTO VARCHAR(2000),
    FECHA_RESPUESTA DATE
    PRIMARY KEY(N_RECLAMO)
    FOREIGN KEY (N_RECLAMO) REFERENCES RECLAMOS(NUMERORECLAMO),
    FOREIGN KEY (RUT) REFERENCES USUARIOS(RUT)
)   
